/*Cabecera Blanca*/
.headerCustom{
    background:#fff;
}

.Logo{
    background-image: url("https://arriagaasociados--c.documentforce.com/servlet/servlet.ImageServer?id=0151t000001tvim&oid=00D1t000000rxg7&lastMod=1570604373000")!important;
    background-size: contain !important;
    background-repeat: no-repeat;
    height:85px;
}

.customHeader .menu-item{
    margin-left: 20px;
    height: -webkit-fill-available;
    padding: 0px;
}

.customHeader div.menu-item.menu-item-has-children>a{
    height:100%;
}
/*Efecto rallita encima*/
.customHeader div.menu-item>a{
  -webkit-transform: translateZ(0);
  transform: translateZ(0);
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
    height: -webkit-fill-available;
  -moz-osx-font-smoothing: grayscale;
  position: relative;
  overflow: hidden;
}
.customHeader div.menu-item>a:before {
    content: "";
    position: absolute;
    z-index: -1;
    left: 0;
    right: 100%;
    top: 0;
    background-color: #ec6806;
    height: 4px;
    -webkit-transition-property: right;
    transition-property: right;
    -webkit-transition-duration: 0.3s;
    transition-duration: 0.3s;
    -webkit-transition-timing-function: ease-out;
    transition-timing-function: ease-out;
  }
 .customHeader div.menu-item>a:hover:before {
    right: 0;
  }
.customHeader .navCommunityCustom{
    max-width: none;
}
.customHeader .contrataonline a{
    background-color: #ec6806;
    color: #fff;
    padding: 10px;
}
.customHeader a{
    color: #000 !important;
    font-size: 11px;
    font-weight: 700;
    line-height: 17px;
    text-decoration: none;
    text-transform: uppercase;
}
.menu-item.menu-item-has-children::after{
    border-bottom: 0;
    border-left: 5px solid transparent;
    border-right: 5px solid transparent;
    border-top: 5px solid #ec6806;
    content: ' ';
    display: inline-block;
    height: 5px;
    margin-left: 5px;
    transition: transform .3s ease-out;
    vertical-align: middle;
    width: 11px;
}
.menu-item.menu-item-has-children:hover::after {
    transform: rotate(180deg);
}
.menu-item-has-children .sub-menu{
    padding: 0px;
    display:none;
}
.menu-item-has-children .sub-menu li:before{
    content:none;
}
.menu-item-has-children .sub-menu li{
    padding-left:0px;
    padding-bottom:1%;
    padding-right:1.5em;
}

.menu-item-has-children:hover .sub-menu{
    display:block;    
    background: #fff;
    top: 5%;
    box-shadow: 0 0 15px rgb(0 0 0 / 29%);
    z-index: 10;
    padding-bottom: 1%;
    padding-top: 1%;
    position: absolute;
}
.sub-menu li a{
    color: #333;
    display: block;
    font-weight: 400;
    line-height: 16px;
    text-transform: none;
}

